        for (int j = 0; j < n; j++){
            Point next = p[(j + 1) % n];
            if (ccw(p[j], next, t) == 0) state = 1;
            else {
                assert(neq(abs(next - t), 0));
                assert(neq(abs(p[j] - t), 0));
                double th = acos(getCos(p[j] - t, next - t)) * ccw(p[j], t, next);
                fsum += th;
            }
        }
