struct OwnVector2
{
	double x, y;
	OwnVector2(double x, double y) :x(x), y(y) {}
	OwnVector2() :OwnVector2(0, 0) {}
	OwnVector2 operator+(const OwnVector2& v)const
	{
		return OwnVector2(x + v.x, y + v.y);
	}
	OwnVector2 operator-(const OwnVector2& v)const
	{
		return OwnVector2(x - v.x, y - v.y);
	}
	OwnVector2 operator*(const double v)const
	{
		return OwnVector2(x*v, y*v);
	}
	bool operator==(const OwnVector2& v)const
	{
		return abs(x - v.x) <= 0.0000000000001&&abs(y - v.y) <= 0.00000000000001;
	}
	bool operator!=(const OwnVector2& v)const
	{
		return abs(x - v.x) > 0.0000000000001 || abs(y - v.y) > 0.00000000000001;
	}

	double dot(const OwnVector2& v)const
	{
		return x*v.x + y*v.y;
	}
	double cross(const OwnVector2& v)const
	{
		return x*v.y - y*v.x;
	}
	double length()const
	{
		return sqrt(x*x + y*y);
	}
	double length2()const
	{
		return x*x + y*y;
	}
};

OwnVector2 vertices[100];

int JudgeType(OwnVector2 p, int n)
{
	int count = 0;
	OwnVector2 p0 = vertices[0];
	bool bFlag0x = (p.x <= p0.x);
	bool bFlag0y = (p.y <= p0.y);

	if ((p0 - p).length2() <= 0.000000000001)
	{
		return 1;
	}

	for (int i = 1; i < n + 1; i++)
	{
		OwnVector2 p1 = vertices[(i) % n];
		if ((p1 - p).length2() <= 0.00000000001 || (abs((p0 - p).cross(p1 - p)) <= 0.00000000000001 && (p0 - p).dot(p1 - p) <= 0.0))
		{
			return 1;
		}
		bool bFlag1x = (p.x <= p1.x);
		bool bFlag1y = (p.y <= p1.y);
		if (bFlag0y != bFlag1y)
		{
			if (bFlag0x == bFlag1x)
			{
				if (bFlag0x)
				{
					count++;
				}
			}
			else
			{
				if (p.x <= (p0.x + (p1.x - p0.x)*(p.y - p0.y) / (p1.y - p0.y)))
				{
					count++;
				}
			}
		}
		p0 = p1;
		bFlag0x = bFlag1x;
		bFlag0y = bFlag1y;
	}

	return count % 2 == 0 ? 0 : 2;
}

int main()
{
	cin.tie(0);
	ios::sync_with_stdio(false);

	int n;
	cin >> n;

	for (int i = 0; i < n; i++)
	{
		double x, y;
		cin >> x >> y;
		vertices[i] = OwnVector2(x, y);
	}

	int q;
	cin >> q;

	for (int i = 0; i < q; i++)
	{
		double px, py;
		cin >> px >> py;
		OwnVector2 p(px, py);

		printf("%d\n", JudgeType(p,n));
	}

	return 0;
}

