bool operator==(pt a, pt b) {
  return (a.x == b.x && a.y == b.y);
}

int isLeft(pt P0, pt P1, pt P2) {
    return ( (P1.x - P0.x) * (P2.y - P0.y)
            - (P2.x -  P0.x) * (P1.y - P0.y) );
}

int xpoly(pt P, vector<pt> V) {
    int n = V.size();
    int    wn = 0;


    for (int i = 0; i<n; i++) {
        if (V[i].y <= P.y) {
            if (V[i + 1].y  > P.y)
            {
                int l = isLeft(V[i], V[i + 1], P);
                if (l > 0)
                    ++wn;
                else if (l == 0)
                    return 1;
            }
        }
        else {
            if (V[i + 1].y <= P.y)
            {
                int l = isLeft(V[i], V[i + 1], P);
                if (l < 0)
                    --wn;
                else if (l == 0)
                    return 1;
            }
        }
    }
    return wn*2;
}

int main(void) {
    int N, u, v;
    cin >> N;
    vector<pt> pts(N+1);
    for (int i = 0; i < N; i++) {
        cin >> u >> v;
        pts[i] = pt{u, v};
    }
    pts[N] = pts[0];

    int Q;
    cin >> Q;
    for (int i = 0; i < Q; i++) {
        cin >> u >> v;
        cout << xpoly(pt{u, v}, pts) << endl;
    }
}

