struct point{
	double x,y;
	point operator+(point p){
		return point{x+p.x,y+p.y};
	}
	point operator-(point p){
		return point{x-p.x,y-p.y};
	}
	point operator*(double p){
		return point{x*p,y*p};
	}
	point operator/(double p){
		if(!p) return point{0,0}; 
		return point{x/p,y/p};
	}
	bool operator==(point p){
		return fabs(x-p.x)<eps&&fabs(y-p.y)<eps;
	}
};

typedef pair<point,point> pp;
typedef vector<point> VP;
const point O{0,0};

class Geom{
	public:
	double Length(point x,point y){
		point z=y-x;
		return sqrt(z.x*z.x+z.y*z.y);
	}
	double IP(point p,point q){
		return p.x*q.x+p.y*q.y;
	}
	double CP(point p,point q){
		return p.x*q.y-q.x*p.y;
	}
	string Counter_Clockwise(pp a,point x){
		if(a.first==x||a.second==x){
			return "On_Segment";
		}
		point A=a.second-a.first;
		point X=x-a.first;
		double ip=IP(A,X),cp=CP(A,X),Al=Length(O,A),Xl=Length(O,X);
		if(cp>eps) return "Counter_Clockwise";
		if(cp<-eps) return "Clockwise";
		if(ip<-eps) return "Online_Back";
		if(Xl<Al||fabs(Xl-Al)<eps) return "On_Segment";
		return "Online_Front";
	}
	string Parallel_Orthogonal(pp a,pp b){
		point A=a.second-a.first,B=b.second-b.first;
		double ip=IP(A,B),cp=CP(A,B);
		string f=Counter_Clockwise(pp(a.first,a.second),b.first),s=Counter_Clockwise(pp(a.first,a.second),b.second);
		if(f[0]!='C'&&s[0]!='C') return "Agreement";
		if(cp<eps&&cp>-eps) return "Parallel";
		if(ip<eps&&ip>-eps) return "Orthogonal";
		else return "Commonly";
	}
	point Inter(pp  a,double x,double y){
		return (a.first*y+a.second*x)/(x+y);
	}
	point Exterior(pp a,double x,double y){
		return (a.first*-y+a.second*x)/(x-y);
	}
	point Projection(pp a,point x){
		point A=x-a.first,B=a.second-a.first;
		double l=Length(O,B);
		return B*IP(A,B)/(l*l)+a.first;
	}	
	point Cross_Point(pp a,pp b){
		string s=Parallel_Orthogonal(a,b);
		if(s[0]=='A') return point{inf,inf};
		if(s[0]=='P') return point{inf,0};
		point p=Projection(a,b.first),q=Projection(a,b.second);
		double pl=Length(b.first,p),ql=Length(b.second,q);
		string S=Counter_Clockwise(a,b.first),T=Counter_Clockwise(a,b.second);
		if(S==T) return Exterior(b,pl,ql);
		else return Inter(b,pl,ql);
	}
	string Containment_polyon_point(VP p,point x){
		point c;
		vector<double> cp(p.size());
		string s,t;
		int ans=0;
		p.push_back(p[0]);
		p.push_back(p[1]);
		cp[0]=CP(point{1,0},p[1]-p[0]);
		for(VP::iterator i=p.begin()+1;i!=p.end()-1;i++){
			pp tmp(*i,*(i+1));
			if(i!=p.end()-2) cp[i-p.begin()]=CP(point{1,0},*(i+1)-*i);
			c=Cross_Point(tmp,pp(x,point{x.x+1,x.y}));
			s=Counter_Clockwise(tmp,x);
			t=Counter_Clockwise(tmp,c);
			if(s[3]=='S') return "On_side";
			double cp1=cp[i-p.begin()],cp2=cp[i-p.begin()-1];
			if(c.x==inf||c.x<x.x||c==*(i+1)||t[3]!='S') continue;
			if(c==*i&&((cp1<=0&&cp2>=0)||(cp1>=0&&cp2<=0))) continue;
			ans++;
		}
		if(ans%2==0) return "not_Containment";
		return "Containment";
	}
	void Point_in(point& p){
		cin>>p.x>>p.y;
	}
	void Point_out(point p){
		cout<<p.x<<' '<<p.y<<endl;
	}
};

int N,Q;
point x;
string s;
VP p;

int main(){
	cout<<syosu(10);
	Geom geo;
	cin>>N;
	p=VP(N);
	for(int i=0;i<N;i++) geo.Point_in(p[i]);
	cin>>Q;
	for(int i=0;i<Q;i++){
		geo.Point_in(x);
		s=geo.Containment_polyon_point(p,x);
		if(s=="Containment") cout<<2<<endl;
		else if(s=="On_side") cout<<1<<endl;
		else cout<<0<<endl;
	}
}

