		int nintersects = 0;
		for(int i = 0; i < n; i++) {
			if(colinear(p, polygon[i], polygon[(i+1)%n])) {
				iscolinear = true;
			}
			else if (segintersect(p, INF, polygon[i], polygon[(i+1)%n])) {
				nintersects++;
			}
		}

		if(iscolinear) {
