        Point t;
        scanf("%lf %lf", &t.x, &t.y);

        int state = 0;
        double fsum = 0;
        for (int j = 0; j < n; j++){
            Point next = p[(j + 1) % n];
            if (ccw(p[j], next, t) == 0) state = 1;
			else {
				double th = acos(getCos(p[j] - t, next - t)) * ccw(p[j], t, next);
				fsum += th;
			}
        }
