int signum(double x) { return abs(x) < EPS ? 0 : x > 0 ? 1 : -1; }

typedef complex<double> P;
typedef vector<P> Poly;

double dot(P a, P b) {
    return a.real() * b.real() + a.imag() * b.imag();
}

double cross(P a, P b) {
    return a.real() * b.imag() - a.imag() * b.real();
}

int point_in_poly(Poly poly, P p) {
    bool in = false;
    int n = poly.size();
    for (int i = 0; i < poly.size(); i++) {
        P a = p - poly[i % n];
        P b = p - poly[(i + 1) % n];
        if (a.imag() > b.imag()) swap(a, b);
        if (a.imag() <= EPS && b.imag() > EPS && cross(a, b) < -EPS) in = !in;
        if (signum(cross(a, b)) == 0 && signum(dot(a, b)) <= 0) return 1;
    }
    return in ? 2 : 0;
}

