#define dTolerance 1.0e-10

struct POINT{
    int x;
    int y;
};

bool LF_POINT_ON_EDGE(const POINT &zp, const POINT &zp0, const POINT &zp1){
    if( (zp0.x - zp.x) * (zp1.y - zp.y) - (zp1.x - zp.x) * (zp0.y - zp.y) != 0){
        return false;
    }
    if(zp0.x == zp.x){
        if(zp.y < min(zp0.y, zp1.y) || zp.y > max(zp0.y, zp1.y)){
            return false;
        }
    } else {
        if(zp.x < min(zp0.x, zp1.x) || zp.x > max(zp0.x, zp1.x)){
            return false;
        }
    }
    return true;
}

int LF_COUNTER_CLOCKWIZE(const POINT &zp, const POINT &zp0){    //zpを通る傾き30000の直線のどちら側にあるか
    if( (zp0.y - zp.y) - 30000 * (zp0.x - zp.x) > 0 ){
        return 1;
    } else if ( (zp0.y - zp.y) - 30000 * (zp0.x - zp.x) < 0 ){
        return -1;
    } else {
        return 0;
    }
}

bool LF_INTERSECT(const POINT &zp, const POINT &zp0, const POINT &zp1){    //zpを通る傾き30000の半直線が辺と交わるか
    if(LF_COUNTER_CLOCKWIZE(zp, zp0) * LF_COUNTER_CLOCKWIZE(zp, zp1) != -1){
        return false;
    }
    if(zp0.x == zp1.x){
        if(zp0.x - zp.x <= 0){
            return false;
        }
    } else if (zp0.x < zp1.x){
        if((zp1.x - zp.x) * (zp0.y - zp.y) - (zp0.x - zp.x) * (zp1.y - zp.y) <= 0){
            return false;
        }
    } else {
        if((zp1.x - zp.x) * (zp0.y - zp.y) - (zp0.x - zp.x) * (zp1.y - zp.y) >= 0){
            return false;
        }
    }
    return true;
}    

//与えられた点p(px,py)に対し、制約より半直線y=30000(x-px)+py (x > px) 上に頂点は存在しない。この直線と交差する辺の本数を数えればよい
int main(){
    int n;
    scanf("%d", &n);
    vector<POINT> vzVertex;
    POINT zVertex0;
    for(int i = 0; i < n; i++){
        POINT zVertex;
        scanf("%d %d", &zVertex.x, &zVertex.y);
        if(i == 0){
            zVertex0.x = zVertex.x;
            zVertex0.y = zVertex.y;
        }
        vzVertex.push_back(zVertex);
    }
    vzVertex.push_back(zVertex0);

    int q;
    scanf("%d", &q);
    for(int j = 0; j < q; j++){
        POINT zPoint;
        scanf("%d %d", &zPoint.x, &zPoint.y);
        int nCount = 0;
        bool bOnEdge = false;
        for (int i = 0; i < n; i++){
            if( LF_POINT_ON_EDGE(zPoint, vzVertex[i], vzVertex[i+1]) ){
                bOnEdge = true;
                break;
            } else {
                if( LF_INTERSECT(zPoint, vzVertex[i], vzVertex[i+1]) ){
                    nCount++;
                }
            }
        }
