		Vector v1 = pg[i] - p;
		Vector v2 = pg[(i + 1)%pg.size()] - p;
		if (((cross(v1, v2) == 0) && (dot(v1, v2) < 0.0)) || norm(v1) == 0 || norm(v2) == 0) return 1;
