int containsSegP(PP p1, PP p2, PP p)   // p1-p2 = seg
{
	double x1 = p1.x, y1 = p1.y, x2 = p2.x, y2 = p2.y;
	if (x1 > x2) {
		double t;
		t = x1, x1 = x2, x2 = t;
		t = y1, y1 = y2, y2 = t;
	}
	return x1 <= p.x && p.x <= x2 &&
		((y1 <= y2 && y1 <= p.y && p.y <= y2) ||
		(y1 > y2 && y2 <= p.y && p.y <= y1))
		&& EQ((p.y - y1)*(x2 - x1), (y2 - y1)*(p.x - x1));
}

// 点が多角形に含まれているかの判定　1:含まれる, 0:含まれない
int containsPolyP(int n, PP *poly, PP p)
{
	int i, f;
	PP a, b, t;
	double c;		// cross(a,b)

	f = 0;
	for (i = 0; i < n; i++) {
		if (containsSegP(poly[i], poly[i+1], p)) return 1;
		a = vsub(poly[i], p), b = vsub(poly[i+1], p);
		if (EQ((c=cross(a, b)), 0) && dot(a, b) < EPS) return 0;
		if (!EQ(a.y, b.y) && a.y >= b.y) t = a, a = b, b = t, c = -c;
		if (a.y < EPS && EPS < b.y && c > EPS) f = !f;
	}
	return f? 2: 0;
}

