	int n;
	scanf("%d",&n);
	for(int i=0;i<n;i++){
		readpoint(p+i);
		s[i].p2=p[i];
		s[(i+1)%n].p1=p[i];
	}
	int q;
	scanf("%d",&q);
	while(q--){
		L t;
		readpoint(&t.p1);
		{
			int flag=0;
			for(int i=0;i<n;i++)if(distPS(t.p1,s[i])==0)flag=1;
			if(flag){
				puts("1");
				continue;
			}
		}
		{
			int flag=1;
			while(flag){
				t.p2.x=rand()%1000+10000;
				t.p2.y=rand()%1000+10000;
				if(seglen(t)==0)continue;
				flag=0;
				for(int i=0;i<n;i++)if(distPS(p[i],t)==0)flag=1;
			}
		}

		int flag2=0;
		for(int i=0;i<n;i++)if(iscrossSS(t,s[i]))flag2^=1;
		puts(flag2?"2":"0");
	}
}

