import java.util.*;
import java.io.*;
import java.lang.*;

public class Main {

	static int INF = 1000000000;
	static int MAXN = 31;

	public static void main(String[] args) {
		Scanner input = new Scanner(System.in);
		int n = input.nextInt();
		int[][] point = new int[n][2];
		for (int i = 0; i < n; ++i) {
			point[i][0] = input.nextInt();
			point[i][1] = input.nextInt();
		}
		int q = input.nextInt();
		for (int i = 0; i < q; ++i) {
			int x, y;
			x = input.nextInt();
			y = input.nextInt();
			System.out.println(checkWithPolygon(x, y, point));
		}
	}

	static int checkWithPolygon(int x, int y, int[][] pol) {
		boolean check = false;
		for (int i = 0; i < pol.length; ++i) {
			int ax = pol[i][0] - x, ay = pol[i][1] - y;
			int bx = pol[(i + 1) % pol.length][0] - x, by = pol[(i + 1) % pol.length][1] - y;
			if (ay > by) {
				int temp = ax; ax = bx; bx = temp;
				temp = ay; ay = by; by = temp;
			}
			if (ay <= 0 && by > 0 && cross(ax, ay, bx, by) < 0) check = !check;
			if (cross(ax, ay, bx, by) == 0) return 1;
		}
		return check ? 2 : 0;
	}

	static int cross(int ax, int ay, int bx, int by) {
		return ax * by - ay * bx;
	}
}

import java.awt.*;
import java.awt.geom.Point2D;
import java.util.ArrayList;
import java.util.Scanner;

class WindingNumber {
    private ArrayList<Point2D> polygon;

    WindingNumber(ArrayList<Point2D> polygon) {
        this.polygon = polygon;
    }

    private boolean eq(double a,double b) {
        return Math.abs(a-b) < 0.0001;
    }

    public int isContain(Point2D target) {
        int wn = 0;
        int sz = polygon.size();
        for(int i = 0; i < polygon.size() ; i++){
            // 上向きの辺、下向きの辺によって処理が分かれる。
            // 上向きの辺。点Pがy軸方向について、始点と終点の間にある。ただし、終点は含まない。(ルール1)
            if ( (polygon.get(i).getY() <= target.getY()) && (polygon.get((i+1)%sz).getY() > target.getY()) ) {
                // 辺は点pよりも右側にある。ただし、重ならない。(ルール4)
                // 辺が点pと同じ高さになる位置を特定し、その時のxの値と点pのxの値を比較する。
                double vt = (target.getY() - polygon.get(i).getY()) / (polygon.get((i+1)%sz).getY() - polygon.get(i).getY());
                if(eq(target.getX(),(polygon.get(i).getX() + (vt * (polygon.get((i+1)%sz).getX() - polygon.get(i).getX()))) )){
                    return 1;
                }
                else if(target.getX() < (polygon.get(i).getX() + (vt * (polygon.get((i+1)%sz).getX() - polygon.get(i).getX())))){
                    wn++;  //ここが重要。上向きの辺と交差した場合は+1
                }
            }
            // 下向きの辺。点Pがy軸方向について、始点と終点の間にある。ただし、始点は含まない。(ルール2)
            else if ( (polygon.get(i).getY() > target.getY()) && (polygon.get((i+1)%sz).getY() <= target.getY()) ) {
                // 辺は点pよりも右側にある。ただし、重ならない。(ルール4)
                // 辺が点pと同じ高さになる位置を特定し、その時のxの値と点pのxの値を比較する。
                double vt = (target.getY() - polygon.get(i).getY()) / (polygon.get((i+1)%sz).getY() - polygon.get(i).getY());
                if(eq(target.getX() ,(polygon.get(i).getX() + (vt * (polygon.get((i+1)%sz).getX() - polygon.get(i).getX()))))) {
                    return 1;
                }
                else if(target.getX() < (polygon.get(i).getX() + (vt * (polygon.get((i+1)%sz).getX() - polygon.get(i).getX())))){
                    --wn;  //ここが重要。下向きの辺と交差した場合は-1
                }
            }
            // ルール1,ルール2を確認することで、ルール3も確認できている。
        }

        if (wn == 1) {
            return 2;
        }
        else {
             return 0;
        }
    }
}

public class Main{
    public static void main(String[] args){
        Scanner stdIn = new Scanner(System.in);
        int n = stdIn.nextInt();
        ArrayList<Point2D> polygon = new ArrayList<Point2D>();
        for(int i=0;i<n;i++){
            double x = stdIn.nextDouble();
            double y = stdIn.nextDouble();
            polygon.add(new Point2D.Double(x, y));
        }
        WindingNumber solve = new WindingNumber(polygon);
        int q = stdIn.nextInt();
