			boolean in = false;
			int n = plist.length;
			for(int i=0; i<n; i++){
				Point2D a = sub(plist[i], p);
				Point2D b = sub(plist[(i+1) % n], p);
				if(a.getY() > b.getY()){
					Point2D temp = (Point2D) a.clone();
					a = b;
					b = temp;
				}
				if(a.getY() <= 0 && 0 < b.getY()){
					if(cross(a,b) < 0) in = ! in;
				}
				if(cross(a,b) == 0 && dot(a, b) <= 0) return 0;
			}
