using Enu = System.Linq.Enumerable;

public class Program
{
    public void Solve()
    {
        int N = Reader.Int();
        var poly = new Polygon(Enu.Range(0, N).Select(i => new Point(Reader.Int(), Reader.Int())));
        int NQ = Reader.Int();

        for (int q = 0; q < NQ; q++)
        {
            var P = new Point(Reader.Int(), Reader.Int());
            Console.WriteLine(PointInPolygon(P, poly));
        }

        Console.ReadLine();
    }

    public const int Outside = 0, OnSegment = 1, Inside = 2;

    public int PointInPolygon(Point p, Polygon poly)
    {
        bool inside = false;
        for (int i = 0; i < poly.N; i++)
        {
            var a = poly[i] - p;
            var b = poly[i + 1] - p;
            if (a.Y > b.Y) { var t = a; a = b; b = t; }
            var det = a.Det(b);
            if (det == 0 && a.Dot(b) <= 0) return OnSegment;
            if (det < 0 && a.Y <= 0 && b.Y > 0) inside = !inside;
        }
        return inside ? Inside : Outside;
    }

    public class Polygon
    {
        public readonly Point[] P;
        public Polygon(Point[] points) { P = points; }
        public Polygon(IEnumerable<Point> points) { P = points.ToArray(); }
        public int N { get { return P.Length; } }
        public Point this[int i] { get { return P[(i % N + N) % N]; } }

        public double Area() { return Math.Abs(SignedArea()); }
        public double SignedArea()
        {
            double area = 0;
            for (int i = 0; i < P.Length; i++)
                area += P[i].Det(P[(i + 1) % P.Length]);
            return area / 2;
        }
        public bool IsConvex()
        {
            for (int i = 1; i < P.Length; i++)
                if ((P[i] - P[i - 1]).Det(P[(i + 1) % P.Length] - P[i]) < 0)
                    return false;
            return true;
        }
    }

    public struct Point : IEquatable<Point>, IComparable<Point>
    {
        public static readonly double Eps = 1e-10;
        public double X, Y;
        public Point(double x, double y) { X = x; Y = y; }
        public static Point operator +(Point a, Point b)
        {
            return new Point(Add(a.X, b.X), Add(a.Y, b.Y));
        }
        public static Point operator -(Point a, Point b)
        {
            return new Point(Add(a.X, -b.X), Add(a.Y, -b.Y));
        }
        public static Point operator *(Point p, double d)
        {
            return new Point(p.X * d, p.Y * d);
        }
        public double Distance(Point b) { return Math.Sqrt((b - this).Dot(b - this)); }
        public double Dot(Point b) { return Add(X * b.X, Y * b.Y); }
        public double Det(Point b) { return Add(X * b.Y, -Y * b.X); }
        public bool Equals(Point b) { return X == b.X && Y == b.Y; }
        public int CompareTo(Point b) { return X != b.X ? Math.Sign(X - b.X) : Math.Sign(Y - b.Y); }
        public override int GetHashCode() { return (int)(X * 100000 + Y); }
        public override string ToString() { return X + ", " + Y; }

        private static double Add(double a, double b)
        {
            if (Math.Abs(a + b) < Eps * (Math.Abs(a) + Math.Abs(b))) return 0;
            return a + b;
        }
    }
}


