INSIDE = 2
ON_EDGE = 1
OUTSIDE = 0


def solve(N, P, A):
    P = [p - A for p in P]

    min_v = Vec(-1, 0)
    for p in P:
        if p == Vec(0, 0):
            return ON_EDGE

        v = Vec(p.x, p.y)
        if v.y == 0:
            continue
        if v.y < 0:
            v = -v

        if min_v.cross(v) < 0:
            min_v = v

    assert min_v != Vec(-1, 0)

    u = min_v + Vec(1, 0)

    neg_count = 0
    for i in range(N):
        a = P[i]
        b = P[(i + 1) % N]

        c_a = u.cross(a)
        c_b = u.cross(b)
        assert c_a != 0
        assert c_b != 0
        if c_a * c_b > 0:
            continue

        ba = b - a
        k = Fraction(a.cross(ba), u.cross(ba))
        if k == 0:
            return ON_EDGE
        if k < 0:
            neg_count += 1

    if neg_count % 2 == 1:
        return INSIDE
    return OUTSIDE


