    pair<Point, Point> crossPoints( Circle c ) {
	double d = ( c.center - center ).abs();
	double cos_alpha = ( r * r + d * d - c.r * c.r ) / ( 2.0 * r * d );
	double alpha = acos( cos_alpha );
	double t = ( c.center - center ).arg();
