int isHit(const polygon& a, const polygon& b){
	if(contains(a, b)) return A_IN_B;
	if(contains(b, a)) return B_IN_A;
	rep(i, a.size()) rep(j, b.size()){
		L la(curr(a, i), next(a, i)), lb(curr(b, j), next(b, j));
		if(intersectSS(la, lb)) return HIT;
