struct Circle {
    Vec O; double r;
    void read() {
        O.read(); scanf("%lf", &r);
    }
};

vector<Vec> isCC(Circle C1, Circle C2) {
    vector<Vec> ans;
    double d = (C1.O - C2.O).len();

    if (C1.r + C2.r < d - EPS) return ans;
    if (abs(C1.r - C2.r) > d + EPS) return ans;

    double cos1 = (C1.r * C1.r + d * d - C2.r * C2.r) / (2 * C1.r * d);
    Vec v1 = (C2.O - C1.O).change(C1.r * cos1);
    Vec v2 = v1.rotate(acos(-1)/2).change(C1.r * sqrt(1 - cos1*cos1));
    ans.push_back(C1.O + v1 + v2);
    ans.push_back(C1.O + v1 - v2);
    return ans;
} 
vector<Vec> tanCP(Circle C, Vec P) {
    double x = (C.O - P).len();
    double d = x * x - C.r * C.r;
    vector<Vec> ans;
    if (d < -EPS) return ans;
    if (d < 0) d = 0;
    Vec q1 = (P - C.O) * ( (C.r * C.r) / (x * x) );
    Vec q2 = q1.rotate(acos(-1) / 2).change(C.r / x * (sqrt(x * x - C.r * C.r)));
    ans.push_back(C.O + q1 + q2);
    ans.push_back(C.O + q1 - q2);
