    double d = (C1.O - C2.O).len();
    if (C1.r + C2.r > d + EPS) {
        return C1.r * C1.r * acos(-1) + C2.r * C2.r * acos(-1);
    }
    if (abs(C1.r - C2.r) < d - EPS) {
        return max(C1.r, C2.r) * max(C1.r, C2.r) * acos(-1);
    }
    double ans = C1.r * C1.r * acos(-1) + C2.r * C2.r * acos(-1);
    double cos1 = (C1.r * C1.r + d * d - C2.r * C2.r) / (2 * C1.r * d);
    double the1 = 2 * acos(cos1);

    double cos2 = (C2.r * C2.r + d * d - C1.r * C1.r) / (2 * C2.r * d);
    double the2 = 2 * acos(cos2);

    ans = ans - ( (2 * acos(-1) - the1) * C1.r * C1.r - sin(the1) * C1.r * C1.r / 2 );
    ans = ans - ( (2 * acos(-1) - the2) * C2.r * C2.r - sin(the2) * C2.r * C2.r / 2 );
    return ans;
}   
