    int n = g.size();
    Point o(0.0, 0.0);
    double s = 0.0;
    for (int i = 0; i < n; i++) s += cross(g[i] - o, g[(i + 1) % n] - o);
    return abs(s) / 2.0;
}

const double INF = 1e10;

typedef pair<double, double> p;
p v[100000];

bool comp_y(p a, p b) {
    return a.second < b.second;
}

double closest_pair(p *a, int n) {
    if (n <= 1) return INF;

    int m = n / 2;
    double x = a[m].first;
    double d = min(closest_pair(a, m), closest_pair(a + m, n - m)); // x軸で区切られた範囲内での頂点対のみ考慮
    inplace_merge(a, a + m, a + n, comp_y); // ソート済みの[a, a+m)と[a+m, a+n)をy座標基準でマージ

    vector<p> b; // 直線 x = a[m].firstから距離d未満の点を入れる
    for (int i = 0; i < n; i++) {
        if (fabs(a[i].first - x) >= d) continue; // そもそもx座標の差がd以上の点は論外

        // bに入っている頂点を末尾から、y座標の差がd以上になるまで見ていく
        // y座標降順で見ていき、d以上離れた時点で打ち切り
        for (int j = 0; j < b.size(); j++) {
            double dx = a[i].first - b[b.size() - 1 - j].first;
            double dy = a[i].second - b[b.size() - 1 - j].second;
            if (dy >= d) break;
            d = min(d, sqrt(dx * dx + dy * dy));
        }

        b.push_back(a[i]);
    }
    return d;
}


struct STsum {
private:
    int n;
    vector<int> dat;
public:
    STsum(int siz) {
        n = 1;
        while (n < siz) n *= 2;
        dat.resize(2 * n - 1, 0);
    }

    void update(int x, int val) {
        x += (n - 1);
        dat[x] += val;
        while (x > 0) {
            x = (x - 1) / 2;
            dat[x] = dat[2 * x + 1] + dat[2 * x + 2];
        }
    }

    // focus on k-th node, who controls [l, r)
    int query(int a, int b, int k = 0, int l = 0, int r = -1) {
        if (r < 0) r = n;
        if (r <= a || b <= l) return 0;
        if (a <= l && r <= b) return dat[k];

        int lx = query(a, b, 2 * k + 1, l, (l + r) / 2);
        int rx = query(a, b, 2 * k + 2, (l + r) / 2, r);
        return lx + rx;
    }
};

