class Matrix1 {

public:
	static const int M = INF;
	int n;
	mat mul(mat& A, mat& B) {
		mat C(A.size(), vl(B[0].size()));
		for (size_t i = 0; i < A.size(); i++)
		{
			for (size_t k = 0; k < B.size(); k++)
			{
				for (size_t j = 0; j < B[0].size(); j++)
				{
					C[i][j] = (C[i][j] + A[i][k] * B[k][j]) % M;
				}
			}
		}
		return C;
	}

	mat pow(mat A, ll n) {
		mat B(A.size(), vl(A.size()));
		for (size_t i = 0; i < A.size(); i++)
		{
			B[i][i] = 1;
		}
		while (n > 0) {
			if (n & 1) B = mul(B, A);
			A = mul(A, A);
			n >>= 1;
		}


		return B;
	}

};
