ll partitionMemo[20010][110];
ll partitionNum(ll v, ll k) {
	if (k == 1) return 1;
	if (v <= 1) return 1;
	if (~partitionMemo[v][k]) return partitionMemo[v][k];
	ll r = 0;
	if (v < k) {
		r = partitionNum(v,v);
	}
	else
		r = partitionNum(v, k - 1) + partitionNum(v - k, k);
	r %= INF;

	return partitionMemo[v][k] = r;
}

class SetTree1 {

public:
	static const int MAX_N = 100000;
	static const int MAX_Q = 100000;
	int N, Q;
	static const int DAT_SIZE = (1 << 18) - 1;
	int A[MAX_N];
	char T[MAX_Q];

	ll data[DAT_SIZE];
	void init(int _n) {
		memset(data, 0, sizeof(data));
		int p = 1;
		while (p < _n)
		{
			p <<= 1;
		}
		N = p;
		Q = N - 1;
	}
	void update(int a, int b) {

		for (size_t i = a; i <= b; i++)
		{
			update(Q + i);
		}
	}
	void update(int a) {

		int x = data[a];
		while (a > 0)
		{
			if (a % 2 == 0)a--;
			a >>= 1;
			data[a] += x;
		}
	}
	void add(int a, int b, int x) {
		add(a, b + 1, x, 0,0,N);
	}
	void add(int a, int b, int x, int k, int l, int r) {
		if (a <= l && r <= b) {
			data[k] += x;
		}
		else if (l < b && a < r) {
			data[k] += (min(b, r) - max(a, l)) * x;
			add(a, b, x, k * 2 + 1, l, (l + r) / 2);
			add(a, b, x, k * 2 + 2, (l + r) / 2, r);
		}
	}
	ll sum(int a, int b) {
		return sum(a, b + 1, 0, 0, N);
	}
	ll sum(int a, int b, int k, int l, int r) {
		if (b <= l || r <= a) return 0;
		else if (a <= l && r <= b) {
			return data[k];
		}
		else {
			ll res = 0;
			res += sum(a, b, k * 2 + 1, l, (l + r) / 2);
			res += sum(a, b, k * 2 + 2, (l + r) / 2, r);
			return res;
		}
	}

};
