}

pair<Vector, Vector> cross_circles(Circle c1, Circle c2) {
  auto l = c2.v - c1.v;
  auto d = distance(c1.v, c2.v);
  auto h = c1.v + l * (d *(c1.r /(c1.r + c2.r)) / l.abs());
  return cross_circle_line(c1, Line(h, h + rotate90(l)));
}

void sort_ans(Vector &a, Vector &b) {
  if (equals(a.x - b.x, 0)) {
    if (b.y < a.y)
      swap(a, b);
  } else {
    if (b.x < a.x)
      swap(a, b);
  }
}

