    double operator*(const Point &t) // 内积
    {
        return (x * t.x + y * t.y);
    }
    double operator^(const Point &t) // 叉积
    {
        return (x * t.y - y * t.x);
    }
    bool operator==(const Point &t) // 判断是否相等
    {
        return equals((x - t.x), 0.0) && equals((y - t.y), 0.0);
