}

bool intersect_ss(Line l,Line m) {
    return ccw(l.a,m.a,l.b)*ccw(l.a,m.b,l.b)<=0 && ccw(m.a,l.a,m.b)*ccw(m.a,l.b,m.b)<=0;
}

bool crosspoint_ll(Line l,Line m,Point &p){
    Point a1,a2,b1,b2;
	a1 = l.b-l.a; a2 = m.b-m.a;
	b1 = m.a-l.a; b2 = m.b-l.a;
	double s1,s2;
	s1 = abs(cross(a1,b1)/2); s2 = abs(cross(a1,b2)/2);
	if(s1<EPS && s2<EPS) return false;
	p = Point(m.a.x+a2.x*s1/(s1+s2),m.a.y+a2.y*s1/(s1+s2));
	return true;
}

bool crosspoint_ss(Line l,Line m,Point &p){
    if(intersect_ss(l,m)==false) return false;
	return crosspoint_ll(l,m,p);
}

int crosspoint_cc(Circle c1,Circle c2,Point &p1,Point &p2){
	double d,a,t;
	d = sqrt((c2.c.x-c1.c.x)*(c2.c.x-c1.c.x)+(c2.c.y-c1.c.y)*(c2.c.y-c1.c.y));
	if(d<abs(c1.r-c2.r) || c1.r+c2.r<d) return 0;
	a = acos((c1.r*c1.r+d*d-c2.r*c2.r)/(2*c1.r*d));
	t = atan2(c2.c.y-c1.c.y,c2.c.x-c1.c.x);
	p1 = Point(c1.r*cos(t+a),c1.r*sin(t+a));
	p2 = Point(c1.r*cos(t-a),c1.r*sin(t-a));
	if(abs(p1.x-p2.x)<EPS && abs(p1.y-p2.y)<EPS) return 1;
	return 2;
}

