        Circle circle1(x1, y1, r1);
        Circle circle2(x2, y2, r2);
        vector<Point> points = circle1.getIntersections(circle2);
        if (points.size() == 1) {
            points.push_back(points.back());
        }
        if (points.front().x > points.back().x || (points.front().x == points.back().x && points.front().y > points.back().y)) {
            swap(points[0], points[1]);
        }
        printf("%.10lf %.10lf ", points[0].x, points[0].y);
        printf("%.10lf %.10lf\n", points[1].x, points[1].y);
    }
    return 0;
}

