pair<P,P> crosspointCC(C c1,C c2) {
  P p1=c1.p,p2=c2.p;
  D r1=c1.r,r2=c2.r;
  P A = conj(p2-p1), B = (r2*r2-r1*r1-(p2-p1)*conj(p2-p1)), C = r1*r1*(p2-p1);
  P D1 = B*B-(D)4.0*A*C;
  P z1 = (-B+sqrt(D1))/((D)2.0*A)+p1, z2 = (-B-sqrt(D1))/((D)2.0*A)+p1;
  if(z2<z1)swap(z1,z2);
  return pair<P,P>(z1, z2);
}

