}

Point project(Segment s, Point p)
{
    Vector hypo = p - s.p1;
    Vector base = s.p2 - s.p1;
    return base * (dot(hypo, base) / base.norm()) + s.p1;
}

Point reflect(Segment s, Point p)
{
    return (project(s, p) - p) * 2.0 + p;
}

double getDistance(Point a, Point b)
{
    return (a - b).abs();
}

double getDistanceLP(Line l, Point p)
{
    return abs(cross(l.p2 - l.p1, p - l.p1)) / (l.p2 - l.p1).abs();
}

double getDistanceSP(Segment s, Point p)
{
    if (dot(s.p2 - s.p1, p - s.p1) < 0.0)
        return getDistance(p, s.p1);
    else if (dot(s.p1 - s.p2, p - s.p2) < 0.0)
        return getDistance(p, s.p2);
    else
        return getDistanceLP(s, p);
}

static const int COUNTER_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2;
static const int ONLINE_FRONT = -2;
static const int ON_SEGMENT = 0;

int ccw(Point p0, Point p1, Point p2)
{
    Vector a = p1 - p0;
    Vector b = p2 - p0;
    if (cross(a, b) > 0.0)
        return COUNTER_CLOCKWISE;
    else if (cross(a, b) < 0.0)
        return CLOCKWISE;
    else if (dot(a, b) < 0.0)
        return ONLINE_BACK;
    else if (a.norm() < b.norm())
        return ONLINE_FRONT;
    else
        return ON_SEGMENT;
}

bool intersect(Point p0, Point p1, Point p2, Point p3)
{
    return ccw(p0, p1, p2) * ccw(p0, p1, p3) <= 0
           && ccw(p2, p3, p0) * ccw(p2, p3, p1) <= 0;
}

double getDistance(Segment s1, Segment s2)
{
    if (intersect(s1.p1, s1.p2, s2.p1, s2.p2))
        return 0.0;
    else
        return min(min(getDistanceSP(s1, s2.p1), getDistanceSP(s1, s2.p2)),
                   min(getDistanceSP(s2, s1.p1), getDistanceSP(s2, s1.p2)));
}

Point getCrossPoint(Segment s1, Segment s2)
{
    double d1 = getDistanceLP(s2, s1.p1);
    double d2 = getDistanceLP(s2, s1.p2);
    double t = d1 / (d1 + d2);
    return s1.p1 + (s1.p2 - s1.p1) * t;
}

pair<Point, Point> CrossPoints(Circle c, Line l)
{
    Point foot = project(l, c.c);
    double scale = sqrt(c.r * c.r - (foot - c.c).norm());
    Vector e = (l.p2 - l.p1) / (l.p2 - l.p1).abs();
    return make_pair(foot + e * scale, foot - e * scale);
}

pair<Point, Point> getRotatePoints(Point x, Point center, double cosine)
{
    double sine = sqrt(1 - cosine * cosine);
    Vector v = x - center;
    Vector u, u_inv;
    u.x = cosine * v.x - sine * v.y;
    u.y = sine * v.x + cosine * v.y;
    u_inv.x = cosine * v.x + sine * v.y;
    u_inv.y = -sine * v.x + cosine * v.y;
    return make_pair(u + center, u_inv + center);
}

pair<Point, Point> getCrossPoints(Circle c1, Circle c2)
{
    double d = (c2.c - c1.c).abs();
    double cosine = (c1.r * c1.r + d * d - c2.r * c2.r) / (2 * c1.r * d);
    Point x = (c2.c - c1.c) * (c1.r / d) + c1.c;
    return getRotatePoints(x, c1.c, cosine);
}

