Point crosspoint(const Line &a,const Line &b){
	assert(intersect(a,b));
	const Real crs = cross(a[1]-a[0],b[1]-b[0]);
	if(sign(crs) == 0) return a[0];
	return b[0]+(b[1]-b[0])*(cross(a[1]-a[0],a[1]-b[0])/crs);
}


