template<typename T>
Point<T> rotateVector(const Point<T>& p, const T rad)
{
	const T c = cos(rad);
	const T s = sin(rad);

	Point<T> ret;
	ret.x = p.x*c - p.y*s;
	ret.y = p.x*s + p.y*c;
	return ret;
}

template<typename T>
array<Point<T>, 2> getIntersectionOfCircles(const Circle<T>& c1, const Circle<T>& c2)
{

	const Point<T> v = c2.center - c1.center;
	const T d = absolute(v);
	const T rad = getRadianFromCosineLaw(c2.radius, c1.radius, d);
	const Point<T> unitV = unit(v);

	array<Point<T>, 2> ret;
	ret.at(0) = c1.center + rotateVector(unitV, rad)*c1.radius;
	ret.at(1) = c1.center + rotateVector(unitV, -rad)*c1.radius;
	return ret;
}

