  D angle = atan2(c2.center.y - c1.center.y, c2.center.x - c1.center.x);
  D d = distance(c1.center, c2.center);
  D add = acos((d * d + c1.radius * c1.radius - c2.radius * c2.radius) / (2.0 * d * c1.radius));
  for(D a: {angle + add, angle - add}) cp.push_back(c1.center + c1.radius * Point(std::cos(a), std::sin(a)));
  return cp;
}

