typedef complex<double> point;

vector<point> CrossPoints_CC(point a,double ra,point b,double rb){
  vector<point> res;
  b-=a;
  if(abs(b)>ra+rb)return res;
  double c=(ra+rb+abs(b))/2;
  c=2*sqrt(c*(c-ra)*(c-rb)*(c-abs(b)))/abs(b);
  double d=sqrt(ra*ra-c*c);
  res.push_back(point(d,c));
  if(c)res.push_back(point(d,-c));
  else res.push_back(point(d,c));
  for(int i=0;i<(int)res.size();i++)res[i]=res[i]*b/abs(b)+a;
  return res;
}

