//外積
double cross(Vector a,Vector b){return a.x*b.y-a.y*b.x;}
//直交判定
bool isOrthgonal(Vector a,Vector b){return equals(dot(a,b), 0.0);}
//平行判定
bool isParallel(Vector a,Vector b){return equals(cross(a,b), 0.0);}
//射影
Point project(Segment s,Point p){
  Vector base=s.p2-s.p1;
  double r=dot(p-s.p1,base)/norm(base);
  return s.p1+base*r;
}
//反射
Point reflect(Segment s,Point p){return p+(project(s,p)-p)*2.0;}
//反時計周りCounter-Clockwise
int ccw(Point p0,Point p1,Point p2){
  Vector a=p1-p0;
  Vector b=p2-p0;
  if(cross(a,b)>EPS) return COUNTER_CLOCKWISE;
  if(cross(a,b)<-EPS) return CLOCKWISE;
  if(dot(a,b)<-EPS) return ONLINE_BACK;
  if(norm(a)<norm(b)) return ONLINE_FRONT;
  return ON_SEGMENT;
}
//線分の交差判定
bool intersect(Point p1,Point p2,Point p3,Point p4){
  return (ccw(p1,p2,p3)*ccw(p1,p2,p4)<=0 &&ccw(p3,p4,p1)*ccw(p3,p4,p2)<=0);
}
bool intersect(Segment s1,Segment s2){return intersect(s1.p1,s1.p2,s2.p1,s2.p2);}
//点と点の距離
double getDisPP(Point a,Point b){return abs(a-b);}
//直線と点の距離
double getDisLP(Line l,Point p){return abs(cross(l.p2-l.p1,p-l.p1))/abs(l.p2-l.p1);}
//線分と点の距離
double getDisSP(Segment s,Point p){
  if(dot(s.p2-s.p1,p-s.p1)<0.0) return abs(p-s.p1);
  if(dot(s.p1-s.p2,p-s.p2)<0.0) return abs(p-s.p2);
  return getDisLP(s,p);
}
//線分と線分の距離
double getDisSS(Segment s1,Segment s2){
  if(intersect(s1,s2)) return 0.0;
  return min(min(getDisSP(s1,s2.p1),getDisSP(s1,s2.p2)),
             min(getDisSP(s2,s1.p1),getDisSP(s2,s1.p2)));
}
//線分と線分の交点
Point getCrossPoint(Segment s1,Segment s2){
  Vector base =s2.p2-s2.p1;
  double d1=abs(cross(base,s1.p1-s2.p1));
  double d2=abs(cross(base,s1.p2-s2.p1));
  double t=d1/(d1+d2);
  return s1.p1+(s1.p2-s1.p1)*t;
}
//円と直線の交点
pair<Point,Point> getCrossPoint(Circle c,Line l){
  Vector pr=project(l,c.c);
  Vector e=(l.p2-l.p1)/abs(l.p2-l.p1);
  double base=sqrt(c.r*c.r-norm(pr-c.c));
  return make_pair(pr+e*base,pr-e*base);
}
//偏角と極座標
double arg(Vector p){return atan2(p.y,p.x);}
Vector polar(double a,double r){return Point(cos(r)*a,sin(r)*a);}
//円と円の交点
pair<Point,Point>getCrossPoint(Circle c1,Circle c2){
  double d=abs(c1.c-c2.c);
  double a=acos((c1.r*c1.r+d*d-c2.r*c2.r)/(2*c1.r*d));
  double t=arg(c2.c-c1.c);
  return make_pair(c1.c+polar(c1.r,t+a),c1.c+polar(c1.r,t-a));
}

