using P = complex<double>;
double dot(P a, P b) { return real(conj(a) * b); }
double cross(P a, P b) { return imag(conj(a) * b); }
const double eps = 1e-8;

double dist_p_p(P a, P b) {
    return sqrt(pow(a.real() - b.real(), 2) + pow(a.imag() - b.imag(), 2));
}
double dist_p_l(P p, P p0, P p1) {
    // ax + by + c = 0
    double x0 = p0.real(), y0 = p0.imag(), x1 = p1.real(), y1 = p1.imag();

    double a = y1 - y0;
    double b = x0 - x1;
    double c = y0 * (x1 - x0) - (y1 - y0) * x0;

    double d = abs(a * p.real() + b * p.imag() + c) / sqrt(a * a + b * b);
    return d;
}

pair<P, P> inter_c_c(P p, P p2, double r, double r2) {
    double d = dist_p_p(p, p2);

    // 中心どうしの角度
    double t = arg(p2 - p);
    // 2 円の交点と p の角度
    // 余弦定理
    double a = acos((r * r + d * d - r2 * r2) / (2 * r * d));

    P p3 = P(p.real() + r * cos(t + a), p.imag() + r * sin(t + a));
    P p4 = P(p.real() + r * cos(t - a), p.imag() + r * sin(t - a));
    return make_pair(p3, p4);
}


void solve() {
    double x[2], y[2], r[2];
    rep(i, 2) cin >> x[i] >> y[i] >> r[i];
    pair<P, P> p3 = inter_c_c(P(x[0], y[0]), P(x[1], y[1]), r[0], r[1]);
    if (p3.F.imag() > p3.S.imag()) swap(p3.F, p3.S);
    if (p3.F.real() > p3.S.real()) swap(p3.F, p3.S);
    cout << p3.F.real() << " " << p3.F.imag() << " " << p3.S.real() << " " << p3.S.imag() << endl;
}

