	d = sqrt((c2.c.x-c1.c.x)*(c2.c.x-c1.c.x)+(c2.c.y-c1.c.y)*(c2.c.y-c1.c.y));
	if(d<abs(c1.r-c2.r) || c1.r+c2.r<d) return 0;
	a = acos((c1.r*c1.r+d*d-c2.r*c2.r)/(2*c1.r*d));
	t = atan2(c2.c.y-c1.c.y,c2.c.x-c1.c.x);
	p1 = Point(c1.r*cos(t+a),c1.r*sin(t+a));
	p2 = Point(c1.r*cos(t-a),c1.r*sin(t-a));
	if(abs(p1.x-p2.x)<EPS && abs(p1.y-p2.y)<EPS) return 1;
	return 2;
}

