vector <PT> CircleCircleIntersection(PT a, PT b, T r, T R) {
	vector <PT> ret;
	T d = sqrt(dist2(a, b));
	if (d > r+R || d+min(r, R) < max(r, R)) return ret;
	T x = (d*d-R*R+r*r)/(2*d);
	T y = sqrt(r*r-x*x);
	PT v = (b-a)/d;
	ret.push_back(a+v*x + RotateCCW90(v)*y);
	if (y > 0)
		ret.push_back(a+v*x - RotateCCW90(v)*y);
	return ret;
}



