	Point operator - (Point p) {
		return Point(x - p.x, y - p.y);
	}
	Point operator * (double k) {
		return Point(k * x, k * y);
	}
	Point operator / (double k) {
		return Point(x / k, y / k);
	}

	double norm() {
		return x * x + y * y;
	}
	double abs() {
		return sqrt(norm());
	}

	bool operator < (const Point &p) const {
		return x != p.x ? x < p.x : y < p.y;
	}

	bool operator == (const Point &p) const {
		return fabs(x - p.x) < EPS && fabs(y - p.y) < EPS;
	}

	Point orth() {
		return Point(-y, x) / abs();
	}
};

typedef Point Vector;

double dot(Vector a, Vector b) {
	return a.x * b.x + a.y * b.y;
}

double cross(Vector a, Vector b) {
	return a.x * b.y - a.y * b.x;
}

class Segment {
public:
	Point p1, p2;
	Segment(Point p1, Point p2): p1(p1), p2(p2) {};

	double norm() {
		return (p2 - p1).x * (p2 - p1).x + (p2 - p1).y * (p2 - p1).y;
	}

	double abs() {
		return sqrt(norm());
	}

	Point project(Point p) { 
		Vector a = Vector(p.x - p1.x, p.y - p1.y);
		Vector b = Vector(p2.x - p1.x, p2.y - p1.y);
		return p1 + (p2 - p1) * (dot(a, b) / b.norm()); //???????????????
	}

	Point refrect(Point p) {
		Point pro = project(p);
		return pro * 2 - p;
	}
};

typedef Segment Line;

class Circle {
public:
	double cx, cy, r;
	Circle(double cx, double cy, double r): cx(cx), cy(cy), r(r) {}

	Segment crossPoints(Line l) {
		Point c = Point(cx, cy);
		Point p = l.project(c);
		double d2 = (p - c).norm();
		double t = sqrt(r * r - d2);
		Point p1 = p - (l.p2 - l.p1) / l.abs() * t;
		Point p2 = p + (l.p2 - l.p1) / l.abs() * t;
		if (p1 < p2) return Segment(p1, p2);
		else return Segment(p2, p1);
	}

	Segment crossPoints(Circle c) {
		Point c1 = Point(cx, cy);
		Point c2 = Point(c.cx, c.cy);
		Point p = c1 + (c2 - c1) * r / (r + c.r);
		Point n = Point((c2 - c1).x, (c2 - c1).y).orth();
		double t = sqrt(r * r - (p - c1).norm());
		Point p1 = p - n * t;
		Point p2 = p + n * t;
		if (p1 < p2) return Segment(p1, p2);
		else return Segment(p2, p1);
	}
};

