double getDistanceLP(Line l,Point p){
  return abs((cross(l.p2 - l.p1,p - l.p1) / abs(l.p2 - l.p1)));
}

// Segment : Point
double getDistanceSP(Segment s,Point p){
  if(dot(s.p2 - s.p1,p - s.p1) < 0.0){
    return abs(p - s.p1);
  }
  if(dot(s.p1 - s.p2,p - s.p2) < 0.0){
    return abs(p - s.p2);
  }
  return getDistanceLP(s,p);
}


double getDistance(Segment s1,Segment s2){
  if(intersect(s1,s2)){
    return 0.0;
  }
  double ans = INF;
  chmin(ans,getDistanceSP(s1,s2.p1));
  chmin(ans,getDistanceSP(s1,s2.p2));
  chmin(ans,getDistanceSP(s2,s1.p1));
  chmin(ans,getDistanceSP(s2,s1.p2));
  return ans;
}

bool intersect(Circle c,Line l){
  return c.r - getDistanceLP(l,c.c) > -EPS;
}

//円と直線の交点
pair<Point,Point> getCrossPoint(Line l,Circle c){
  assert(intersect(c,l));
  Vector pr = project(l,c.c);
  Vector e = (l.p2 - l.p1) / abs(l.p2 - l.p1);
  if(e.x < 0 || (e.x < EPS && e.y < 0)){
    e = e * -1;
  }
  double base = sqrt(c.r * c.r - norm(pr - c.c));
  return make_pair(pr - e * base,pr + e * base);
}

bool intersect(Circle c1,Circle c2){
  double dis = getDistance(c1.c,c2.c);
  return c1.r + c2.r - dis > -EPS && abs(c1.r - c2.r) - dis < EPS;
}

double arg(Vector p){
  return atan2(p.y,p.x);
}

Vector polar(double a,double r){
  return Point(cos(r) * a ,sin(r) * a);
}

//円と円の交点
pair<Point,Point> getCrossPoint(Circle c1,Circle c2){
  assert(intersect(c1,c2));
  double d = abs(c1.c-c2.c);
  double a = acos((c1.r * c1.r + d * d - c2.r * c2.r) / (2 * c1.r * d));
  double t = arg(c2.c - c1.c);
  //cout << d << a << t;
  return make_pair(c1.c + polar(c1.r, t+a), c1.c + polar(c1.r,t-a));
}

