struct Segment {
	Point p1, p2;
	Segment() {}
	Segment(Point p1, Point p2) :p1(p1), p2(p2){}
	double slope(){
		if(p2 < p1)swap(p1, p2);
		if(p1.x == p2.x){
			return -1e-9;
		}
		return (p2.y - p1.y)/(p2.x - p1.x);
	}
};

