struct Circle {
    Point p;
    double r;
    Circle(Point p = Point(), double r = 0) : p(p), r(r) {}
};

vector<Point> intersection(Circle c1, Circle c2) {
    double d = abs(c1.p - c2.p);
    vector<Point> res;
    if(sgn(c1.r+c2.r - d) < 0)return res;
    double rc = (d*d + c1.r*c1.r - c2.r*c2.r) / (2*d);
    double rs = sqrt(c1.r*c1.r - rc*rc);
    Point e = (c2.p - c1.p) / d;
    res.push_back(c1.p+e*Point(rc, rs));
    Point p = c1.p+e*Point(rc, -rs);
    if(res[0] == p)return res;
    res.push_back(p);
    if(res[1] < res[0])swap(res[1], res[0]);
    return res;
}

