class Circle {
public:
    Point center;
    double r;

    Circle( Point center = Point(), double r = 0 ): center(center), r(r) {}

    pair<Point, Point> crossPoints( Line l ) {
	Point proj = l.projection( center ); // ?????????????????´???????°???±
	double base = sqrt( r * r - ( center - proj ).norm() );
	Vector e = ( l.p2 - l.p1 ) / ( l.p2 - l.p1 ).abs();
	if ( l.p2 < l.p1 ) e = e * -1; // x?????£????????????????????????
	return make_pair( proj - e * base, proj + e * base );
    }

    pair<Point, Point> crossPoints( Circle c ) {
	Circle c1 = ( this->center < c.center ) ? *this : c;
	Circle c2 = ( this->center < c.center ) ? c : *this;	
	double d = ( c2.center - c1.center ).abs();
	double cos_alpha = ( c1.r * c1.r + d * d - c2.r * c2.r ) / ( 2.0 * c1.r * d );
	double alpha = acos( cos_alpha );
	double t = ( c2.center - c1.center ).arg();
	Point p1 = center + polar( r, t - alpha );
	Point p2 = center + polar( r, t + alpha );

	return ( p1 < p2 ) ? make_pair( p1, p2 ) : make_pair( p2, p1 );
    }
};


