    ansx1 = c1.p.x + (double)c1.r*(cosa*cost - sint*sina);
    ansy1 = c1.p.y + (double)c1.r*(sint*cosa + cost*sina);
    ansx2 = c1.p.x + (double)c1.r*(cosa*cost + sint*sina);
    ansy2 = c1.p.y + (double)c1.r*(sint*cosa - cost*sina);

