pair<vec,vec> cross_point(vec C1, vec C2, double r1, double r2){
    vec e1,e2,P1,P2;
    double d=abs(C2-C1);
    e1=(C2-C1)*(1/d);
    double theta = acos((r1*r1+norm(C2-C1)-r2*r2)/(2*r1*d));
    P1=C1+rotate(e1,theta)*r1;
    P2=C1+rotate(e1,-theta)*r1;
    if(P1.real()==P2.real()){
        if(P1.imag()<P2.imag()) return make_pair(P1,P2);
        else return make_pair(P2,P1);
    }else{
        if(P1.real()<P2.real()) return make_pair(P1,P2);
        else return make_pair(P2,P1);
    }
}

