typedef complex<double> P;
typedef pair<P, double> Circle;

#define EPS 1e-8
#define EQ(a, b) (abs((a)-(b)) < EPS)

inline int signum(double x) { return abs(x) < EPS ? 0 : x > 0 ? 1 : -1; }

pair<P, P> inter_circles(Circle c1, Circle c2) {
    P p1 = c1.first;
    P p2 = c2.first;
    double r1 = c1.second;
    double r2 = c2.second;
    double d = abs(p1 - p2);
    double rc = (r1 * r1 + d * d - r2 * r2) / (2 * d);
    double rs = sqrt(r1 * r1 - rc * rc);
    P ans1 = p1 + (p2 - p1) / d * P(rc, rs);
    P ans2 = p1 + (p2 - p1) / d * P(rc, -rs);
    return make_pair(ans1, ans2);
}

int main() {

    double c1x, c1y, c1r, c2x, c2y, c2r;
    cin >> c1x >> c1y >> c1r >> c2x >> c2y >> c2r;

    Circle c1(P(c1x, c1y), c1r);
    Circle c2(P(c2x, c2y), c2r);

    auto ret = inter_circles(c1, c2);

    P p1 = ret.first;
    P p2 = ret.second;

    if (p1.real() > p2.real()) swap(p1, p2);
    else if (p1.real() == p2.real() && p1.imag() > p2.imag()) swap(p1, p2);

    cout << fixed << setprecision(20) << p1.real() << " " << p1.imag() << " " << p2.real() << " " << p2.imag() << endl;

}

