struct Point {
  let x, y;
  let dist() {
    return sqrt(x * x + y * y);
  }
  Point Rotate(let sink, let cosk) {
    return (Point) {
      x * cosk - y * sink, x * sink + y * cosk
    };
  }
  Point Rotate(Point v) {
    let z = v.dist();
    return Rotate(v.y / z, v.x / z);
  }
  friend Point operator + (Point a, Point b) {
    return (Point) {a.x + b.x, a.y + b.y};
  }
  friend Point operator - (Point a, Point b) {
    return (Point) {a.x - b.x, a.y - b.y};
  }
  Point operator ~ () {
    return (Point) {x, -y};
  }
  friend bool operator < (Point a, Point b) {
    if (sign(a.x - b.x) == 0) return sign(a.y - b.y) < 0;
    return sign(a.x - b.x) < 0;
  }
  void EchoWith(char c) {
    printf("%.8Lf %.8Lf", x, y);
    putchar(c);
  }
};

typedef Point Vec;

struct Circle {
  Point o;
  let r;
  friend pair<Point, Point> Cross(Circle a, Circle b) {
    Vec v = b.o - a.o;
    let r1 = a.r, r2 = b.r, d = (b.o - a.o).Rotate(~v).x;
    if (sign(r1 + r2 - d) == 0) {
      Point p = ((Point) {r1, 0}).Rotate(v) + a.o;
      return make_pair(p, p);
    }

    let x = d / 2 + (r1 * r1 - r2 * r2) / (2 * d),
        y = sqrt(r1 * r1 - x * x);
    Point p1 = ((Point) {x, y}).Rotate(v) + a.o, 
          p2 = ((Point) {x, -y}).Rotate(v) + a.o;
    if (p2 < p1) swap(p1, p2);
    return make_pair(p1, p2);
  }
};

Point ReadPoint() {
  Point p;
  scanf("%Lf%Lf", &p.x, &p.y);
  return p;
}

Circle ReadCirle() {
  let c;
  Point p = ReadPoint();
  scanf("%Lf", &c);
  return (Circle) {p, c};
}

