#define EPS (1e-10)
#define NEXT(x, i) (x[(i + 1) % x.size()])

template<class T> using CR = const T &;
using P = complex<ld>;
using G = vector<P>;

int sgn(ld a, ld b = 0) {
    if (a > b + EPS) return 1;
    if (a < b - EPS) return -1;
    return 0;
}

ld dot(P a, P b) { return real(conj(a)*b); }
ld cross(P a, P b) { return imag(conj(a)*b); }

int ccw(P a, P b, P c) {
    b -= a;
    c -= a;
    if (sgn(cross(b, c))) return sgn(cross(b, c)); // clockwise or counter clockwise
    if (sgn(dot(b, c)) == -1) return 2;            // c--a--b
    if (sgn(norm(b), norm(c)) == -1) return -2;    // a--b--c
    return 0;                                      // a--c--b
}

struct L : public vector<P> {
    L(P a, P b) { push_back(a); push_back(b); }
};


struct C {
    P p; ld r;
    C(P p, ld r) : p(p), r(r) { }
};
pair<P, P> pCC(CR<C> c1, CR<C> c2) {
    ld d = abs(c1.p - c2.p);
    ld rc = (d*d + c1.r*c1.r - c2.r*c2.r) / (2*d);
    ld rs = sqrt(c1.r*c1.r - rc*rc);
    P diff = (c2.p - c1.p) / d;
	return { c1.p + diff * P(rc, rs), c1.p + diff * P(rc, -rs) };
}

