	void Make_Vector(Vector A, Point B)
	{
		x = B.x - A.x;
		y = B.y - A.y;
	}

	ld Size()
	{
		return sqrt(Add(Sqr(x), Sqr(y)));
	}

	void Normalize()
	{
		ld len = Size();
		x /= len;	
		y /= len;
	}

