struct Line
{
	ld a, b, c;

	void Make_Line(Point A, Point B)
	{
		a = B.y - A.y;
		b = -(B.x - A.x);
		c = A.y * (B.x - A.x) - A.x * (B.y - A.y);
	}

	ld Distance_Point_Line(Point M)
	{
		ld ans = abs(a * M.x + b * M.y + c);
		ld res = sqrt(a * a + b * b);
		return ans / res;
	}

	void Make_Perpendicular(Vector M, Line L)
	{
		a = L.b;
		b = -L.a;
		c = -L.b * M.x + L.a * M.y;
	}
};

bool Less(ld l, ld r)
{
	return l + eps < r;
}

bool More(ld l, ld r)
{
	swap(l, r);
	return Less(l, r);
}

bool Equal(ld l, ld r)
{
	return abs(l - r) < eps;
}

