    for(int i=0;i<2;i++) cin>>c[i].c.x>>c[i].c.y>>c[i].r;
    Point cp[2];
    crosspoint_cc(c[0],c[1],cp[0],cp[1]);
    sort(cp,cp+2);
    printf("%.8lf %.8lf %.8lf %.8lf\n",cp[0].x,cp[0].y,cp[1].x,cp[1].y);
}

