}

pair<Vector2, Vector2> crossPoints(Circle c1, Circle c2){
    double d = abs(c1.c - c2.c);
    double a = acos((c1.r * c1.r + d * d - c2.r * c2.r) / (2 * c1.r * d));
    double t = arg(c2.c - c1.c);
    return make_pair(c1.c + c1.r * polar(t + a), c1.c + c1.r * polar(t - a));
}

